var neo4j = require('neo4j-driver')


  var session;



export default class Neo4j{

    constructor(){
        this.driver = neo4j.driver(
            'bolt://localhost:7687',
            neo4j.auth.basic('neo4j', 'BDD')
          );
    }
   

    

    connect(){
        session = this.driver.session()

        session
  .run(' MATCH (n:Utilisateur {prenom: $nameParam}) return n LIMIT 10', {
    nameParam: 'Brigitte'
  })
  .then(result => {
    result.records.forEach(record => {
        console.log("USER:")
      console.log(record.get('n')+ " " + record.get('nom'))
    })
  })
  .catch(error => {
    console.log(error)
  })
  .then(() => session.close())
  return "fait";
    }

    

}
