import React from "react";
import styled from "styled-components";
import Neo4j from './Neo4j';

const Button = styled.button`
  background-color: black;
  color: white;
  font-size: calc(10px + 2vmin);
  padding: 10px 60px;
  border-radius: 5px;
  margin: 10px 0px;
  cursor: pointer;
  margin: 20px;
`;


var Neo4jInstance = new Neo4j();



export default class Queries extends React.Component {

    constructor(props){
        super(props);
        this.state = {loading:false,timeReq1:false};
    }

    async getInfluenceurProducts(i){
        
        this.setState({...this.state,loading:true});
        var t1 = new Date();
        await Neo4jInstance.getInfluenceurProducts(i);
        var t2 = new Date();
        var dif = t1.getTime() - t2.getTime();
        var Seconds_from_T1_to_T2 = dif / 1000;
        var Seconds_Between_Dates = Math.abs(Seconds_from_T1_to_T2);

        this.setState({...this.state,loading:false,timeReq1:Seconds_Between_Dates});
    }

    render() {
      return (
      <div>
        <p> SQL<br></br>
        
        <Button onClick={()=>this.getInfluenceurProducts(986)}>Requête 1</Button>
        <a>{this.state.loading && "Chargement"}</a>
        <a>{this.state.timeReq1 && "Temps de la requête " + this.state.timeReq1 + " s"}</a>
        <Button>Requête 2</Button>
        <Button >Requête 3</Button>
        
        </p>
        <br></br>
        <br></br>
        <p> NoSQL<br></br> 
            <Button>Requête 1</Button>
            <Button >Requête 2</Button>
            <Button>Requête 3</Button>
        </p>
      </div>
    

    );
  }

}
